export const newGame = () => {
  const { username } = store.getState().user.p1;
  if (username === 'anonymous') {
    createBoard();
    setRandomPieces('p1');
    setRandomPieces('p2');
    const { p1Pieces, p2Pieces } = store.getState().board;
    updatePieces('p1', p1Pieces);
    updatePieces('p2', p2Pieces);
  } else {
    axios.post(`${url}/games`)
      .then(response => {
        const { data } = response;
        store.dispatch({
          type: 'setInfo',
          payload: { id: data },
        });
        createBoard();
        setRandomPieces('p1');
        setRandomPieces('p2');
        setUser('p1', {games: [data]});
        setUser('p2', {games: [data]});
        store.dispatch({
          type: 'setInfo',
          payload: { id: data },
        });
        const board = store.getState().board;
        setBoard(board);
        const { p1Pieces, p2Pieces } = board;
        updatePieces('p1', p1Pieces);
        updatePieces('p2', p2Pieces);
      });
  }
};

/**
 * Performs a 'guess' action on a single tile. Depending on whether or not there is
 * bread on that tile, different actions will occur.
 * @param {string} player either 'p1' or 'p2'. Represents TARGET player's board.
 * @param {string} id the ID of the guessed tile, ex: '1,1' or '4,3'
 */
export const guess = (player, id) => {
  store.dispatch({
    type: 'guess', 
    payload: { player, id },
  });
  store.dispatch({ type: 'toggleTurn' });
  const { p1Pieces, p2Pieces } = store.getState().board;
  store.dispatch({
    type: 'updatePieces',
    payload: { player: 'p1', pieces: p1Pieces },
  });
  store.dispatch({
    type: 'updatePieces',
    payload: { player: 'p2', pieces: p2Pieces },
  });
  if (!!store.getState().gameInfo.id) {
    updateGame(store.getState().gameInfo.id);
  }
};

import update from 'immutability-helper';
import randomInt from 'random-int';
import { range } from 'lodash';

const newState = { 
  p1: {}, 
  p2: {}, 
  turn: 0, 
  p1Pieces: 0,
  p2Pieces: 0,
};

/**
 * This function handles changes to the board state resulting from a state.dispatch(action)
 * @param { object } state state is an object representing each players' board. Example:
 * @property { object } state
 * @property { number } state.turn Turn counter
 * @property { number } state.p1Pieces Number of remaining pieces for player 1
 * @property { number } state.p2Pieces Number of remaining pieces for player 2
 * @property { object } state.p1 Tile states for player 1
 * @property { object } state.p1[tileId] Tile object, indexed by id string
 * @property { object } state.p1[tileId].id Id string (ex: '1,1' or '6,5')
 * @property { number } state.p1[tileId].row
 * @property { number } state.p1[tileId].col
 * @property { boolean } state.p1[tileId].hasBread
 * @property { boolean } state.p1[tileId].guessed
 * @property { string } state.p1[tileId].color 'red' || 'green' || 'blue'
 * @property { object } state.p2 Tile states for player 2
 * @property { object } state.p2[tileId] Tile object, indexed by id string
 * @property { object } state.p2[tileId].id Id string (ex: '1,1' or '6,5')
 * @property { number } state.p2[tileId].row
 * @property { number } state.p2[tileId].col
 * @property { boolean } state.p2[tileId].hasBread
 * @property { boolean } state.p2[tileId].guessed
 * @property { string } state.p2[tileId].color 'red' || 'green' || 'blue'
 *
 * 
 * @param { object } action an action dispatched via an action creator from ../actions.js
 * @property { string } type 'guess' || 'randomPieces' || 'setPiece
 * @property { object } payload varies in shape for each action
 * @returns a new state, based on the type of action it receives
 */
const boardReducer = (state = { ...newState }, { type, payload = {} } = action) => {
  if (type === 'createBoard') {
    /**
     * We start a new board from scratch. 
     * 1. Generate an 8x8 array for each player
     * 2. Map a tile object to each index by extending the defaults object with row and col info.
     */

    range(8).map((row) => {
      range(8).map((col) => {
        const defaults = {
          size: '48px',
          guessed: false,
          hasBread: false,
          color: 'blue',
        };
        newState.p1[`${row},${col}`] = {
          ...defaults,
          row,
          col,
          id: `${row},${col}`,
        };
        newState.p2[`${row},${col}`] = {
          ...defaults,
          row,
          col,
          id: `${row},${col}`,
        };
      });
    });
    return update(state, {$merge: newState});
  } else if (type === 'setBoard') {
    const { board } = payload;
    return update(state, { $merge: board });
  } else if (type === 'guess') {
    /**
     * @param { object } payload
     * @property { string } player 'p1' || 'p2'
     * @property { string } id ex: '1,1' or '3,4'
     * 
     * shape: { 
     *  player: {'p1' or 'p2'},
     *  id: { string (ex:'1,1' or '3,4') }
     * }
     * We update the state for one tile on a particular player's board
     * 1. Increment the turn count
     * 2. Set the guessed property for that tile to true
     * 3. Change the color property to green if it was a hit, or red if it was a miss
     * 4. If it was a hit, we decrement that player's piece count
     * 
     */
    const { player, id } = payload;
    const { turn } = state;
    const { hasBread } = state[player][id];
    const numPieces = state[`${player}Pieces`];

    const newState = update(state, {
      turn: {$set: (turn + 1)},
      [player]: {
        [id]: {
          guessed: {$set: true},
          color: {$apply: () => (hasBread) ? 'green' : 'red'}
        }
      },
      [`${player}Pieces`]: {$apply: () => (hasBread) ? numPieces - 1 : numPieces },
    });
    // newState[player][id] = tile;
    return newState;
  } else if (type === 'randomPieces') {
    /**
     * This is mostly just for testing. Updates 14 random tiles to contain bread.
     */
    const player1 = {};
    const player2 = {};

    range(14).forEach(() => {
      const [row1, row2, col1, col2] = range(4).map(() => randomInt(7));

      player1[`${row1},${col1}`] = update(state.p1[`${row1},${col1}`], {
        hasBread: {$set: true}
      });
      player2[`${row2},${col2}`] = update(state.p2[`${row2},${col2}`], {
        hasBread: {$set: true}
      });

    });
    return update(state, {
      turn: { $set: 0 },
      p1: { $merge: player1 },
      p2: { $merge: player2 },
    });
  } else if (type === 'setPiece') {
    /**
     * @param payload
     *  @property { string } player 'p1' || 'p2'
     *  @property { array } piece array of tile id strings (ex: ['1,1', '2,4'])
     * Place a piece by setting hasBread for each tile to true
     */
      const { player, piece } = payload;
      const thePiece = {};
      const numPieces = state[`${player}Pieces`] + piece.length;

      piece.forEach(idString => {
        thePiece[idString] = update(
          state[player][idString],
          { hasBread: { $set: true }}
        );
      });

      return update(state, {
        [player]: { $merge: thePiece },
        [`${player}Pieces`]: { $set: numPieces },
      });
  } else {
    /**
     * Fallback case
     */
    return state;
  }
};

export default boardReducer;

import update from 'immutability-helper';

/**
 * This function handles changes to the chats state resulting from a state.dispatch(action)
 * @param { array } state chat state is an array of chat objects. Default: []
 * @param { object } action an action obj with shape { type: { string }, payload: { obj }}
 * @returns a new state, based on the type of action it receives
 */
const chatReducer = (state = [], { type, payload } = action) => {
  if (type === 'setChat') {
    /**
     * @param payload shape: { player: { 'p1' || 'p2' }, text{ string } }
     * @returns a new state array with the chat obj passed in appended to the end
     */
    return update(state, { $push: [payload] });
  } else if (type === 'getChats') {
    /**
     * @returns the entire state array
     */
    return state;
  } else {
    /**
     * fallback case
     */
    return state;
  }
};

export default chatReducer;

import update from 'immutability-helper';

const defaultGameInfo = {
  id: null,
  status: 'inactive',
  turn: 'p1',
  p1Pieces: 0,
  p2Pieces: 0
};

/**
 * Reducer function for the gameInfo portion of the redux state
 * @param { object } state
 * @property { string } state.status 'active' || 'inactive'
 * @property { string } state.turn 'p1' || 'p2'
 * @property { number } state.p1Pieces number of pieces remaining for p1
 * @property { number } state.p2Pieces number of pieces remaining for p2
 * @returns a new state, based on the type of action it receives
 */
const gameInfoReducer = (state = { ...defaultGameInfo }, { type, payload } = action) => {
  if (type === 'toggleStatus') {
    /**
     * Toggles whether the game state is active or not
     * @param { string } payload.status 'active' || 'inactive'
     */
    const { status } = payload;
    return update(state, {
      status: {
        $apply: () => (status === 'active') ? 'inactive' : 'active' 
      }
    });
  } else if (type === 'toggleTurn') {
    /**
     * Toggles which player's turn it is
     * @param { string } payload.turn 'p1' || 'p2'
     */
    const { turn } = state;
    return update(state, {
      turn: {
        $apply: () => (turn === 'p1') ? 'p2' : 'p1' 
      }
    });
  } else if (type === 'updatePieces') {
    /**
     * Updates a player's piece count
     * @param { string } payload.player 'p1' || 'p2'
     * @param { number } payload.pieces The updated piece count
     */
    const { player, pieces } = payload;
    return update(state, {
      [`${player}Pieces`]: { $set: pieces },
    });
  } else if (type === 'getInfo') {
    return state;
  } else if (type === 'setInfo') {
    // const { newInfo } = payload;
    return update(state, { $merge: payload });
  } else {
    return state;
  }
};

export default gameInfoReducer;

